mapper.select:
SELECT * FROM product_version
WHERE product_id = #{product_id}
AND organization_id = #{organization_id}

SELECT * FROM product_version
WHERE product_id = #{product_id}
AND organization_id = #{organization_id}
AND version = #{version}

SELECT * FROM product_version
WHERE product_id = #{product_id}
AND organization_id = #{organization_id}
AND is_current = 'Y'

SELECT * FROM product_version
WHERE product_id = #{product_id}
AND organization_id = #{organization_id}
ORDER BY version DESC LIMIT 1;

mapper.update:
UPDATE product_version
SET
effective_date = #{effective_date},
plans = #{plans}
WHERE product_id = #{product_id}
AND organization_id = #{organization_id}
AND version = #{version}

UPDATE product_version
SET
is_current = 'Y'
WHERE product_id = #{product_id}
AND organization_id = #{organization_id}
AND version = #{version}

UPDATE product_version
SET
is_current = 'N'
WHERE product_id = #{product_id}
AND organization_id = #{organization_id}
AND is_current = 'Y'

mapper.delete:
DELETE FROM product_version
WHERE product_id = #{product_id}
AND organization_id = #{organization_id}
AND version = #{version}
